package ua.hillel.tsaryk.lessons.lesson17;import java.util.Scanner;public class Main {        public static void main(String[] args) {            Scanner scanner = new Scanner(System.in);            boolean done = false;            while (!done) {                System.out.println("Выберите напиток из списка: ");                for (DrinksMachine drink : DrinksMachine.values()) {                    System.out.print(drink + " ");                }                System.out.print("Введите ваш выбор: ");                String input = scanner.nextLine();                try {                    DrinksMachine choice = DrinksMachine.valueOf(input.toUpperCase());                    switch (choice) {                        case COFFEE:                            Drinks.makeCoffee();                            break;                        case TEA:                            Drinks.makeTea();                            break;                        case LEMONADE:                            Drinks.makeLemonade();                            break;                        case MOJITO:                            Drinks.makeMojito();                            break;                        case MINERAL_WATER:                            Drinks.makeMineralWater();                            break;                        case COCA_COLA:                            Drinks.makeCocaCola();                            break;                    }                } catch (IllegalArgumentException e) {                    System.out.println("Такого напитка нет в списке!");                }                System.out.print("Хотите заказать еще один напиток? (да/нет): ");                input = scanner.nextLine();                if (input.equalsIgnoreCase("нет")) {                    done = true;                }            }            System.out.println("Вы заказали " + Drinks.totalDrinks + " напитков.");            System.out.println("Ваш счет составляет " + Drinks.totalPrice + " гривен.");        }}